mapper.select:
select * from
(select rownum rn, tt.* from
(select * from t_shopping_qna order by ref desc, step asc)tt
)where rn  >=  #{startNum} and rn  <=  #{endNum}


select * from
(select rownum rn, tt.* from
(select * from t_shopping_qna where ${searchType} like '%'||#{keyword}||'%' order by ref desc, step asc)tt
)where rn>=#{startNum} and rn<=#{endNum}
